PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE users (id INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, username TEXT NOT NULL, hash TEXT NOT NULL);
CREATE TABLE studios (id INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, name TEXT NOT NULL, address TEXT NOT NULL);
CREATE TABLE classes (id INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, studio_id INTEGER NOT NULL, description TEXT NOT NULL, date_time TEXT, rate INTEGER NOT NULL, FOREIGN KEY(studio_id) REFERENCES studios(id));
CREATE TABLE monthly_summary (id INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, studio INTEGER, title TEXT, date_time TEXT, month TEXT, class_id INTEGER, FOREIGN KEY(studio) REFERENCES studios(id));
CREATE TABLE weekly_summary (id INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, studio_id INTEGER NOT NULL, description TEXT NOT NULL, date_time TEXT, rate INTEGER NOT NULL, FOREIGN KEY(studio_id) REFERENCES studios(id));
COMMIT;
