DROP TABLE IF EXISTS weekly_summary;
DROP TABLE IF EXISTS monthly_summary;
DROP TABLE IF EXISTS classes;
DROP TABLE IF EXISTS studios;
CREATE TABLE IF NOT EXISTS studios (id SERIAL PRIMARY KEY NOT NULL, name TEXT NOT NULL, address TEXT NOT NULL);
CREATE TABLE IF NOT EXISTS classes (id SERIAL PRIMARY KEY NOT NULL, studio_id INTEGER NOT NULL, description TEXT NOT NULL, date_time TIMESTAMP, rate INTEGER NOT NULL, FOREIGN KEY(studio_id) REFERENCES studios(id));
CREATE TABLE IF NOT EXISTS monthly_summary (id SERIAL PRIMARY KEY NOT NULL, studio INTEGER, title TEXT, date_time TIMESTAMP, month TEXT, class_id INTEGER, FOREIGN KEY(studio) REFERENCES studios(id));
CREATE TABLE IF NOT EXISTS weekly_summary (id SERIAL PRIMARY KEY NOT NULL, studio_id INTEGER NOT NULL, description TEXT NOT NULL, date_time TIMESTAMP, rate INTEGER NOT NULL, FOREIGN KEY(studio_id) REFERENCES studios(id));
